package com.framework.webClient.controller;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import com.framework.webClient.service.ILedSjzdManageService;import com.framework.webClient.util.JsonUtils;/** * @author 吉庆 * @date Thu Aug 16 00:00:00 CST 2018 */@RestControllerpublic class LedSjzdController {	@Autowired	private ILedSjzdManageService ledsjzdmanageservice;	/**	 * 通过Id查询详细信息	 * 	 * @param pzdbm 字段父编码 	 *            1:控制模式,2:设备复位重启,3:设备水浸保护,4:分区类型	 *            5:字体尺寸,6:字体风格,7:字体粗细,8:显示方式	 *            9:移动速度,10:是否语音报读,11:报读音量大小,12:报读速度	 *            13:报读次数,14:重复次数	 * @return ID ID	 * @return ZDBM 字段编码	 * @return ZDMC 字段名称	 * @return PZDBM 字段父编码	 * 	 */	@PostMapping(value = "/selectLedSjzdByPzdbm")	public List<Map<String, Object>> selectLedSjzdByPzdbm(String pzdbm) {		List<Map<String, Object>> ledsjzd = ledsjzdmanageservice.selectLedSjzdByPzdbm(pzdbm);		return ledsjzd;	}	/**	 * 通过Id查询详细信息	 * 	 * @param pzdbm 字段父编码 	 *            1:控制模式,2:设备复位重启,3:设备水浸保护,4:分区类型	 *            5:字体尺寸,6:字体风格,7:字体粗细,8:显示方式	 *            9:移动速度,10:是否语音报读,11:报读音量大小,12:报读速度	 *            13:报读次数,14:重复次数	 * @return ID ID	 * @return ZDBM 字段编码	 * @return ZDMC 字段名称	 * @return PZDBM 字段父编码	 * 	 */	@PostMapping(value = "/selectLedSjzdByPzdbmList")	public Map<String, Object> selectLedSjzdByPzdbmList(@RequestParam Map<String, Object> paramMap) {		String  paramString = String.valueOf(paramMap.get("pzdbmList"));		@SuppressWarnings("unchecked")		List<String> paramList = JsonUtils.convertString2Obj(paramString, List.class);		paramMap.put("pzdbmList", paramList);		Map<String, Object> ledsjzd = ledsjzdmanageservice.selectLedSjzdByPzdbmList(paramMap);		return ledsjzd;	}}