package com.framework.webClient.dao;import java.util.List;import java.util.Map;/** * @author 吉庆 * @date Fri Aug 17 00:00:00 CST 2018 */public interface LedTemplateDao {	/**	 * 通过Id查询详细信息	 * 	 * @param mbid 模版id	 * @param mbmc 模版名称	 * @param mbbh 模版编号	 * @param tag 是否模糊查询标识，为空走模糊查询	 */	public List<Map<String, Object>> selectLedTemplateByInfo(Map<String, Object> paramMap);	/**	 * 通过Id删除该条数据	 * 	 * @param mbid 模版ID	 */	public int deleteLedTemplateById(String mbid);	/**	 * 通过Id修改该条数据	 * 	 * @param paramMap 更新参数	 */	public int updateLedTemplateById(Map<String, Object> paramMap);	/**	 * 新增一条数据	 * 	 * @param model 实体封装类	 */	public int insertLedTemplate(Map<String, Object> paramMap);		/**	 * 	 * 分区模版ID和分区类型查询分区区域设置信息	 * 	 * @param mbid 模版ID	 * @param fqlx 分区类型	 * @return	 */	public List<Map<String, Object>> selectQyszByMbidAndFqlx(Map<String, Object> paramMap);		/**	 * 	 * 分区模版ID和分区类型查询分区文件信息	 * 	 * @param mbid 模版ID	 * @param fqlx 分区类型	 * @return	 */	public List<Map<String, Object>> selectFileInfoByMbidAndFqlx(Map<String, Object> paramMap);		/**	 * 	 * 分区模版ID和分区类型查询文件ID列表	 * 	 * @param mbid 模版ID	 * @param fqlx 分区类型	 * @return	 */	public List<String> selectFileidByMbidAndFqlx(Map<String, Object> paramMap);		/**	 * 	 * 根据分区模版ID查询分区ID列表	 * @param mbid 模版id	 * @param mbmc 模版名称	 * @param mbbh 模版编号	 * @return	 */	public List<String> selectFqidByMbidOrMcOrBh(Map<String, Object> paramMap);		/**	 * 	 * 查询分区模版下拉表	 * @return	 */	public List<Map<String, Object>> selectTemplateList(); }