package com.framework.webClient.service;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.framework.webClient.entity.LedCsszb;import com.framework.webClient.service.ILedCsszbManageService;import com.framework.webClient.dao.LedCsszbDao;/** * @author 吉庆 * @date Thu Aug 16 00:00:00 CST 2018 */@Servicepublic class LedCsszbManageServiceImpl implements ILedCsszbManageService {	@Autowired(required = true)	private LedCsszbDao   ledcsszbdao;	/**	 * 通过Id查询详细信息	 * @param mbid  模版id	 * @param mbbh  模版编号	 * @param mbmc  模版名称	 */	@Override	public List<LedCsszb> selectLedCsszbByMbinfo(Map<String, Object> paramMap){		return ledcsszbdao.selectLedCsszbByMbinfo(paramMap);	}	/**	 * 通过Id删除该条数据	 * @param mbid  模版id	 */	@Override	public int  deleteLedCsszbById(String mbid){		return ledcsszbdao.deleteLedCsszbById(mbid);	}	/**	 * 通过Id修改该条数据	 * @param model  实体封装类 	 */	@Override	public int  updateLedCsszbById(LedCsszb ledcsszb){		return ledcsszbdao.updateLedCsszbById(ledcsszb);	}	/**	 * 新增一条数据	 * @param model  实体封装类 	 */	@Override	public int  insertLedCsszb(LedCsszb ledcsszb){		return ledcsszbdao.insertLedCsszb(ledcsszb);	}	/**	 * 查询参数模版下拉列表	 */	@Override	public List<Map<String, Object>> selectCsmbCombox() {		return ledcsszbdao.selectCsmbCombox();	}}