package com.framework.webClient.service;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.framework.webClient.dao.LedSjzdDao;import com.framework.webClient.util.ConstantUtil;/** * @author 吉庆 * @date Thu Aug 16 00:00:00 CST 2018 */@Servicepublic class LedSjzdManageServiceImpl implements ILedSjzdManageService {	@Autowired(required = true)	private LedSjzdDao  ledsjzddao;	/**	 * 通过Id查询详细信息	 * @param pzdbm  字段父编码 	 */	public List<Map<String, Object>> selectLedSjzdByPzdbm(String pzdbm){		return ledsjzddao.selectLedSjzdByPzdbm(pzdbm);	}	/**	 * 	 * 通过Id批量查询详细信息	 * @param paramMap 字段父编码 	 * @return	 */	@Override	public Map<String, Object> selectLedSjzdByPzdbmList(Map<String, Object> paramMap) {		Map<String, Object> resultMap = new HashMap<>();		List<Map<String, Object>> dataList = ledsjzddao.selectLedSjzdByPzdbmList(paramMap);		@SuppressWarnings("unchecked")		List<String> pzdbmList = (List<String>) paramMap.get("pzdbmList");		for (String key : pzdbmList) {			List<Map<String, Object>> resultList = new ArrayList<>();			int i = 0;			for (Map<String, Object> temp : dataList) {				if (key.equals(temp.get("pzdbm"))) {					if (paramMap.containsKey("tag") && i == 0 && !key.equals(ConstantUtil.LEDFX_VALUE) && !key.equals(ConstantUtil.FX_VALUE) 							&& !key.equals(ConstantUtil.ZDTK_VALUE) && !key.equals(ConstantUtil.QYZT_VALUE) && !key.equals(ConstantUtil.QYZD_VALUE)) {						Map<String, Object> qbMap = new HashMap<>();						qbMap.put("zdbm", ConstantUtil.NULL_STRING);						qbMap.put("zdmc", ConstantUtil.COMMON_QUANBU);						resultList.add(0, qbMap);						i++;					}					resultList.add(temp);				}			}			resultMap.put(key, resultList);		}		return resultMap;	};}